<script >
    jQuery(document).ready(function(){
		let dataOrder = getParameterByName('dataOrder');
		numOrder = dataOrder;
		getNumGuia(dataOrder)
		//alert(dataOrder)
    })
    
    function getParameterByName(name) {
	    name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");
	    var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),
	    results = regex.exec(location.search);
	    return results === null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));
	}
	
	
	function getNumGuia(dataOrder) {
	    console.log(dataOrder)
		var dataGuiaEstafeta = new Object();
		    dataGuiaEstafeta.numPedido = dataOrder;

		var dataGuiaEstafetaJson = JSON.stringify(dataGuiaEstafeta);

		jQuery.post('../EstafetaController.php',
		        {
		          action:'getDataGuias',
		          parametros:dataGuiaEstafetaJson,
		        },
		    	function(data,textStatus) {
		    	    //console.log(data)
		    	    let aux0 = data[0].guiaPdf
		    	    let aux = aux0.substring(5,aux0.length - 4)
		    	    //console.log(aux)
		    	    jQuery('#numGuia').val(aux)
		        	rastrearGuia();
		   		 },
		    "json"
		);
			
	}
	
	/*
    jQuery(function(){
        jQuery("#numGuia").keypress(function(e) {
           if(e.which == 13) {
              rastrearGuia()
           }
        });
        
        
        jQuery('#rastrearGuia').click(function(){
