    function rastrearGuia(){
        jQuery('#dataEnvio').empty()
            let numGuia = jQuery('#numGuia').val()
            let numeroGuia = numGuia;
    			
	        let dataGuiaEstafeta = new Object();
		        dataGuiaEstafeta.numeroGuia = numeroGuia;		        
		    let dataGuiaEstafetaJson = JSON.stringify(dataGuiaEstafeta);
	        jQuery.post('../../apiEstafeta/EstafetaController.php',
		    {
		        action:'rastrearGuia',
		        parametros:dataGuiaEstafetaJson,
		    },
		    function(data,textStatus) {
		        console.log(data)
		        
		        let dataArray = Object.values(data);
		        let errorCode = dataArray[0]['errorCode']
		        if(errorCode != '0'){
		            alert('El número de guía es invalido')
		        }else{
		            renderRastreo(dataArray);   
		        }
			        
		    },
		    "json"
		);
    }
    
    function renderRastreo(dataArray){
        
        if(dataArray[0]['trackingData']['TrackingData'] != undefined){
            let puntos = dataArray[0]['trackingData']['TrackingData']['history']['History'];
            console.log(puntos)
            let table = '<table style="border-collapse: collapse">';
            for(let q=0;q<puntos.length;q++){
                let punto = puntos[q];
                table += '<tr>'
                table += '<td>'+punto.eventDateTime+'</td>'
                table += '<td>'+punto.eventDescriptionSPA+'</td>'
                table += '<tr>'
            }
            table += '</table>'
        
            jQuery('#dataEnvio').html(table) 
        }else{
            alert('El número de guia aún no cuenta con información suficiente para detallar sus movimientos, intentelo de nuevo más tarde.')
        }
        
    }
    
